contracts/EntryPoint.sol.content:
pragma solidity ^0.8.20;

import "./interfaces/IUserOperation.sol";
import "./interfaces/IWallet.sol";
import "./interfaces/IPaymaster.sol";
import "./utils/UserOperationLib.sol";
import "@openzeppelin/contracts/utils/introspection/ERC165.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title EntryPoint
 * @dev Main entry point for ERC-4337 Account Abstraction
 * Handles UserOperation validation, execution, and gas accounting
 */
contract EntryPoint is ERC165, ReentrancyGuard {
    using UserOperationLib for IUserOperation.UserOperation;

    // Constants
    uint256 public constant STAKE_MINIMUM = 1 ether;
    uint256 public constant UNSTAKE_DELAY_SEC = 1 days;
    uint256 private constant INNER_OUT_OF_GAS_BUFFER = 50000;

    // State variables
    uint256 public immutable chainId;
    mapping(address => uint256) public balanceOf;

    // Stake management for paymasters
    struct StakeInfo {
        uint256 stake;
        uint256 unstakeDelaySec;
        uint256 withdrawTime;
    }
    mapping(address => StakeInfo) public stakes;

    // Gas tracking for operations
    struct UserOpInfo {
        uint256 preOpGas;
        uint256 prefund;
        bool success;
        uint256 actualGasCost;
    }
    mapping(bytes32 => UserOpInfo) public userOpInfo;

    // Events
    event UserOperationEvent(
        bytes32 indexed userOpHash,
        address indexed sender,
        address indexed paymaster,
        uint256 nonce,
        bool success,
        uint256 actualGasCost,
        uint256 actualGasUsed
    );

    event AccountDeployed(
        bytes32 indexed userOpHash,
        address indexed sender,
        address factory,
        address paymaster
    );

    event Deposited(address indexed account, uint256 totalDeposit);
    event Withdrawn(address indexed account, address withdrawAddress, uint256 amount);
    event StakeLocked(address indexed account, uint256 totalStaked, uint256 unstakeDelaySec);
    event StakeUnlocked(address indexed account, uint256 withdrawTime);
    event StakeWithdrawn(address indexed account, address withdrawAddress, uint256 amount);

    // Custom errors for gas efficiency
    error FailedOp(uint256 opIndex, string reason);
    error SignatureValidationFailed();
    error PaymasterValidationFailed();
    error InvalidStake();
    error StakeTooLow();
    error StakeWithdrawTooSoon();

    constructor() {
        chainId = block.chainid;
    }

    // ERC-165 support
    function supportsInterface(bytes4 interfaceId) public view virtual override returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Main entry point for handling user operations
     * @param ops Array of user operations to execute
     * @param beneficiary Address to receive gas payments
     */
    function handleOps(
        IUserOperation.UserOperation[] calldata ops,
        address payable beneficiary
    ) external nonReentrant {
        uint256 opsLength = ops.length;
        require(opsLength > 0, "EntryPoint: empty ops array");

        uint256 collected = 0;

        for (uint256 i = 0; i < opsLength; i++) {
            collected += _handleOp(i, ops[i], beneficiary);
        }

        if (collected > 0) {
            (bool success,) = beneficiary.call{value: collected}("");
            require(success, "EntryPoint: beneficiary transfer failed");
        }
    }

    /**
     * @dev Simulate validation of a user operation
     * @param userOp The user operation to validate
     * @return validationResult Validation result with gas estimates
     */
    function simulateValidation(
        IUserOperation.UserOperation calldata userOp
    ) external returns (IUserOperation.ValidationResult memory validationResult) {
        uint256 preOpGas = gasleft();

        // Create wallet if needed
        _createWalletIfNeeded(userOp);

        uint256 validationGas = preOpGas - gasleft();

        // Validate with wallet
        uint256 walletValidationData = _validateWallet(userOp, validationGas);

        // Handle paymaster validation if present
        bytes memory paymasterContext;
        uint256 paymasterValidationData = 0;

        if (userOp.paymasterAndData.length > 0) {
            (paymasterValidationData, paymasterContext) = _validatePaymaster(userOp, validationGas);
        }

        // Calculate prefund needed
        uint256 gasUsed = preOpGas - gasleft();
        uint256 prefund = _calculatePrefund(userOp, gasUsed);

        validationResult = IUserOperation.ValidationResult({
            preOpGas: gasUsed,
            prefund: prefund,
            sigFailed: walletValidationData == 1,
            validAfter: uint48(walletValidationData >> 160),
            validUntil: uint48(walletValidationData >> 208),
            paymasterContext: paymasterContext
        });
    }

    /**
     * @dev Handle individual user operation
     */
    function _handleOp(
        uint256 opIndex,
        IUserOperation.UserOperation calldata userOp,
        address payable beneficiary
    ) internal returns (uint256 collected) {
        uint256 preOpGas = gasleft();
        bytes32 userOpHash = userOp.hash(address(this), chainId);

        UserOpInfo storage opInfo = userOpInfo[userOpHash];
        opInfo.preOpGas = preOpGas;

        try this._validateAndExecuteOp(userOp, userOpHash, opIndex) returns (uint256 actualGasCost) {
            opInfo.success = true;
            opInfo.actualGasCost = actualGasCost;
            collected = actualGasCost;
        } catch Error(string memory reason) {
            revert FailedOp(opIndex, reason);
        } catch {
            revert FailedOp(opIndex, "Unknown error");
        }

        uint256 actualGasUsed = preOpGas - gasleft();

        emit UserOperationEvent(
            userOpHash,
            userOp.sender,
            _getPaymasterAddress(userOp),
            userOp.nonce,
            opInfo.success,
            opInfo.actualGasCost,
            actualGasUsed
        );
    }

    /**
     * @dev External function for validation and execution (used in try-catch)
     */
    function _validateAndExecuteOp(
        IUserOperation.UserOperation calldata userOp,
        bytes32 userOpHash,
        uint256 opIndex
    ) external returns (uint256 actualGasCost) {
        require(msg.sender == address(this), "EntryPoint: only self call");

        // Create wallet if needed
        _createWalletIfNeeded(userOp);

        // Validate signatures and nonces
        uint256 validationData = _validateWallet(userOp, 0);
        if (validationData == 1) {
            revert SignatureValidationFailed();
        }

        // Handle paymaster
        bytes memory paymasterContext;
        if (userOp.paymasterAndData.length > 0) {
            uint256 paymasterValidationData;
            (paymasterValidationData, paymasterContext) = _validatePaymaster(userOp, 0);
            if (paymasterValidationData == 1) {
                revert PaymasterValidationFailed();
            }
        }

        // Execute the operation
        uint256 callGasLimit = userOp.callGasLimit;
        if (callGasLimit == 0) callGasLimit = gasleft() - INNER_OUT_OF_GAS_BUFFER;

        bool success = _executeUserOp(userOp.sender, userOp.callData, callGasLimit);

        // Calculate actual gas cost
        actualGasCost = _calculateActualGasCost(userOp, success);

        return actualGasCost;
    }

    /**
     * @dev Create wallet if initCode is provided
     */
    function _createWalletIfNeeded(IUserOperation.UserOperation calldata userOp) internal {
        if (userOp.initCode.length > 0) {
            address factory = address(bytes20(userOp.initCode[0:20]));
            bytes memory initCallData = userOp.initCode[20:];

            (bool success,) = factory.call(initCallData);
            require(success, "EntryPoint: wallet creation failed");

            emit AccountDeployed(
                userOp.hash(address(this), chainId),
                userOp.sender,
                factory,
                _getPaymasterAddress(userOp)
            );
        }
    }

    /**
     * @dev Validate wallet signature and nonce
     */
    function _validateWallet(
        IUserOperation.UserOperation calldata userOp,
        uint256 missingAccountFunds
    ) internal returns (uint256 validationData) {
        try IWallet(userOp.sender).validateUserOp(userOp, userOp.hash(address(this), chainId), missingAccountFunds) 
            returns (uint256 result) {
            return result;
        } catch {
            return 1; // Validation failed
        }
    }

    /**
     * @dev Validate paymaster
     */
    function _validatePaymaster(
        IUserOperation.UserOperation calldata userOp,
        uint256 missingAccountFunds
    ) internal returns (uint256 validationData, bytes memory context) {
        address paymaster = _getPaymasterAddress(userOp);

        // Check paymaster stake
        require(stakes[paymaster].stake >= STAKE_MINIMUM, "Paymaster: insufficient stake");

        try IPaymaster(paymaster).validatePaymasterUserOp(userOp, userOp.hash(address(this), chainId), missingAccountFunds)
            returns (bytes memory _context, uint256 _validationData) {
            return (_validationData, _context);
        } catch {
            return (1, ""); // Validation failed
        }
    }

    /**
     * @dev Execute user operation call data
     */
    function _executeUserOp(
        address wallet,
        bytes calldata callData,
        uint256 callGasLimit
    ) internal returns (bool success) {
        if (callData.length > 0) {
            (success,) = wallet.call{gas: callGasLimit}(callData);
        } else {
            success = true;
        }
    }

    /**
     * @dev Calculate prefund needed for operation
     */
    function _calculatePrefund(
        IUserOperation.UserOperation calldata userOp,
        uint256 gasUsed
    ) internal pure returns (uint256) {
        uint256 gasPrice = userOp.maxFeePerGas;
        uint256 gasLimit = userOp.callGasLimit + userOp.verificationGasLimit + userOp.preVerificationGas;
        return (gasLimit + gasUsed) * gasPrice;
    }

    /**
     * @dev Calculate actual gas cost after execution
     */
    function _calculateActualGasCost(
        IUserOperation.UserOperation calldata userOp,
        bool success
    ) internal pure returns (uint256) {
        uint256 gasPrice = success ? userOp.maxFeePerGas : userOp.maxPriorityFeePerGas;
        return (userOp.callGasLimit + userOp.verificationGasLimit + userOp.preVerificationGas) * gasPrice;
    }

    /**
     * @dev Get paymaster address from paymasterAndData
     */
    function _getPaymasterAddress(IUserOperation.UserOperation calldata userOp) internal pure returns (address) {
        if (userOp.paymasterAndData.length == 0) return address(0);
        return address(bytes20(userOp.paymasterAndData[0:20]));
    }

    // Stake management functions
    function addStake(uint256 unstakeDelaySec) external payable {
        StakeInfo storage info = stakes[msg.sender];
        info.stake += msg.value;
        info.unstakeDelaySec = unstakeDelaySec;
        emit StakeLocked(msg.sender, info.stake, unstakeDelaySec);
    }

    function unlockStake() external {
        StakeInfo storage info = stakes[msg.sender];
        require(info.stake > 0, "EntryPoint: no stake");
        info.withdrawTime = block.timestamp + info.unstakeDelaySec;
        emit StakeUnlocked(msg.sender, info.withdrawTime);
    }

    function withdrawStake(address payable withdrawAddress) external {
        StakeInfo storage info = stakes[msg.sender];
        require(info.withdrawTime > 0 && block.timestamp >= info.withdrawTime, "EntryPoint: stake withdrawal too soon");

        uint256 stake = info.stake;
        info.stake = 0;
        info.withdrawTime = 0;

        (bool success,) = withdrawAddress.call{value: stake}("");
        require(success, "EntryPoint: stake withdrawal failed");

        emit StakeWithdrawn(msg.sender, withdrawAddress, stake);
    }

    // Deposit functions for gas payments
    function depositTo(address account) external payable {
        balanceOf[account] += msg.value;
        emit Deposited(account, balanceOf[account]);
    }

    function withdrawTo(address payable withdrawAddress, uint256 withdrawAmount) external {
        require(balanceOf[msg.sender] >= withdrawAmount, "EntryPoint: insufficient balance");
        balanceOf[msg.sender] -= withdrawAmount;

        (bool success,) = withdrawAddress.call{value: withdrawAmount}("");
        require(success, "EntryPoint: withdrawal failed");

        emit Withdrawn(msg.sender, withdrawAddress, withdrawAmount);
    }

    // Fallback to receive ETH
    receive() external payable {
        balanceOf[msg.sender] += msg.value;
        emit Deposited(msg.sender, balanceOf[msg.sender]);
    }
}

contracts/interfaces/IPaymaster.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IUserOperation.sol";

/**
 * @title IPaymaster
 * @dev Interface for ERC-4337 compatible paymasters
 */
interface IPaymaster {

    enum PostOpMode {
        opSucceeded, // user op succeeded
        opReverted,  // user op reverted. still has to pay for gas.
        postOpReverted //user op succeeded, but caused postOp to revert
    }

    /**
     * @dev Validate paymaster user operation
     * @param userOp User operation to validate
     * @param userOpHash Hash of the user operation
     * @param maxCost Maximum cost that could be paid by this paymaster
     * @return context Paymaster context for post-operation
     * @return validationData Validation result packed with valid time range
     */
    function validatePaymasterUserOp(
        IUserOperation.UserOperation calldata userOp,
        bytes32 userOpHash,
        uint256 maxCost
    ) external returns (bytes memory context, uint256 validationData);

    /**
     * @dev Post-operation hook called after user operation execution
     * @param mode Post-operation mode (opSucceeded, opReverted, postOpReverted)
     * @param context Context data from validatePaymasterUserOp
     * @param actualGasCost Actual gas cost of the operation
     */
    function postOp(
        PostOpMode mode,
        bytes calldata context,
        uint256 actualGasCost
    ) external;

    // TODO: Add other necessary paymaster functions (e.g., withdraw, addStake)
}

contracts/interfaces/IUserOperation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IUserOperation
 * @dev Interface defining the UserOperation structure for ERC-4337
 */
interface IUserOperation {
    /**
     * @dev User operation structure for ERC-4337 account abstraction
     */
    struct UserOperation {
        address sender;
        uint256 nonce;
        bytes initCode;
        bytes callData;
        uint256 callGasLimit;
        uint256 verificationGasLimit;
        uint256 preVerificationGas;
        uint256 maxFeePerGas;
        uint256 maxPriorityFeePerGas;
        bytes paymasterAndData;
        bytes signature;
    }

    struct ValidationResult {
        uint256 preOpGas;
        uint256 prefund;
        bool sigFailed;
        uint48 validAfter;
        uint48 validUntil;
        bytes paymasterContext;
    }
}

contracts/interfaces/IWallet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./IUserOperation.sol";

/**
 * @title IWallet
 * @dev Interface for ERC-4337 compatible smart wallets
 */
interface IWallet {
    /**
     * @dev Validate user operation signature and nonce
     * @param userOp User operation to validate
     * @param userOpHash Hash of the user operation
     * @param missingAccountFunds Amount of ETH needed for gas payment
     * @return validationData Validation result (0 = success, 1 = failure)
     */
    function validateUserOp(
        IUserOperation.UserOperation calldata userOp,
        bytes32 userOpHash,
        uint256 missingAccountFunds
    ) external returns (uint256 validationData);

    /**
     * @dev Execute a transaction from this wallet
     * @param target Target contract address
     * @param value ETH value to send
     * @param data Call data to execute
     * @return success Whether the execution was successful
     */
    function execute(
        address target,
        uint256 value,
        bytes calldata data
    ) external returns (bool success);

    /**
     * @dev Execute multiple transactions in a batch
     * @param targets Array of target addresses
     * @param values Array of ETH values
     * @param data Array of call data
     * @return successes Array of success status for each call
     */
    function executeBatch(
        address[] calldata targets,
        uint256[] calldata values,
        bytes[] calldata data
    ) external returns (bool[] memory successes);

    /**
     * @dev Get the current nonce for this wallet
     * @return Current nonce value
     */
    function getNonce() external view returns (uint256);

    // TODO: Add other necessary wallet functions (e.g., fallback, token recovery)
}

contracts/Paymaster.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./interfaces/IPaymaster.sol";
import "./interfaces/IUserOperation.sol";

/**
 * @title NexusPay Paymaster
 * @dev EIP-4337 compliant paymaster contract that sponsors gas fees for user transactions
 */
contract Paymaster is IPaymaster {

    address public immutable entryPoint;
    address public owner;
    mapping(address => bool) public authorizedSpenders;
    mapping(address => uint256) public spendingLimits; // Per-address daily spending limits
    mapping(address => uint256) public dailySpent; // Track daily spending per address
    mapping(address => uint256) public lastResetDay; // Track last reset day for spending limits

    uint256 public totalDeposit;
    uint256 public totalSpent;
    bool public paused;

    event PaymasterDeposit(address indexed sender, uint256 amount);
    event PaymasterWithdraw(address indexed recipient, uint256 amount);
    event UserOperationSponsored(address indexed sender, uint256 actualGasCost);
    event SpendingLimitSet(address indexed spender, uint256 limit);
    event AuthorizedSpenderAdded(address indexed spender);
    event AuthorizedSpenderRemoved(address indexed spender);
    event PaymasterPaused();
    event PaymasterUnpaused();

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner");
        _;
    }

    modifier onlyEntryPoint() {
        require(msg.sender == address(entryPoint), "Only EntryPoint");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Paymaster is paused");
        _;
    }

    constructor(address _entryPoint, address _owner) {
        entryPoint = _entryPoint;
        owner = _owner;
        paused = false;
    }

    /**
     * @dev Validate paymaster user operation (EIP-4337)
     * @param userOp The user operation to validate
     * @param userOpHash Hash of the user operation
     * @param maxCost Maximum cost that can be charged
     * @return context Data to be passed to postOp
     * @return validationData Packed validation data (authorizer, validUntil, validAfter)
     */
    function validatePaymasterUserOp(
        IUserOperation.UserOperation calldata userOp,
        bytes32 userOpHash,
        uint256 maxCost
    ) external override onlyEntryPoint whenNotPaused returns (bytes memory context, uint256 validationData) {
        // Basic validation - check if we have enough deposit
        require(getDeposit() >= maxCost, "Insufficient deposit");

        address sender = userOp.sender;

        // Check if sender is authorized (optional - can be set to allow all)
        bool isAuthorized = authorizedSpenders[sender] || authorizedSpenders[address(0)]; // address(0) means allow all
        require(isAuthorized, "Sender not authorized");

        // Check spending limits
        uint256 currentDay = block.timestamp / 1 days;
        if (lastResetDay[sender] < currentDay) {
            dailySpent[sender] = 0;
            lastResetDay[sender] = currentDay;
        }

        uint256 limit = spendingLimits[sender];
        if (limit > 0) {
            require(dailySpent[sender] + maxCost <= limit, "Daily spending limit exceeded");
        }

        // Return success validation
        return (abi.encode(sender, maxCost), 0);
    }

    /**
     * @dev Post-operation handler (EIP-4337)
     * @param mode Whether the operation succeeded or reverted
     * @param context Data from validatePaymasterUserOp
     * @param actualGasCost Actual gas cost paid
     */
    function postOp(
        PostOpMode mode,
        bytes calldata context,
        uint256 actualGasCost
    ) external override onlyEntryPoint {
        (address sender, uint256 maxCost) = abi.decode(context, (address, uint256));

        // Update spending tracking
        dailySpent[sender] += actualGasCost;
        totalSpent += actualGasCost;

        emit UserOperationSponsored(sender, actualGasCost);

        // Handle failed operations if needed
        if (mode == PostOpMode.postOpReverted) {
            // Could implement refund logic here
        }
    }

    /**
     * @dev Deposit funds to the paymaster
     */
    function deposit() public payable {
        require(msg.value > 0, "Must deposit some ETH");
        (bool success,) = entryPoint.call{value: msg.value}(abi.encodeWithSignature("depositTo(address)", address(this)));
        totalDeposit += msg.value;
        emit PaymasterDeposit(msg.sender, msg.value);
    }

    /**
     * @dev Get current deposit balance
     */
    function getDeposit() public view returns (uint256) {
        (bool success, bytes memory data) = entryPoint.staticcall(abi.encodeWithSignature("balanceOf(address)", address(this)));
        if (success && data.length >= 32) {
            return abi.decode(data, (uint256));
        }
        return 0;
    }

    /**
     * @dev Withdraw funds from the paymaster (owner only)
     * @param recipient Address to receive the funds
     * @param amount Amount to withdraw
     */
    function withdraw(address payable recipient, uint256 amount) external onlyOwner {
        require(recipient != address(0), "Invalid recipient");
        require(amount <= getDeposit(), "Insufficient balance");

        (bool success,) = entryPoint.call(abi.encodeWithSignature("withdrawTo(address,uint256)", recipient, amount));
        require(success, "EntryPoint withdraw failed");
        emit PaymasterWithdraw(recipient, amount);
    }

    /**
     * @dev Add stake to EntryPoint (required for paymaster operation)
     * @param unstakeDelay Delay before stake can be withdrawn
     */
    function addStake(uint32 unstakeDelay) external payable onlyOwner {
        (bool success,) = entryPoint.call{value: msg.value}(abi.encodeWithSignature("addStake(uint32)", unstakeDelay));
        require(success, "EntryPoint addStake failed");
    }

    /**
     * @dev Unlock stake (first step of withdrawal)
     */
    function unlockStake() external onlyOwner {
        (bool success,) = entryPoint.call(abi.encodeWithSignature("unlockStake()"));
        require(success, "EntryPoint unlockStake failed");
    }

    /**
     * @dev Withdraw stake (after unlock delay)
     * @param recipient Address to receive the stake
     */
    function withdrawStake(address payable recipient) external onlyOwner {
        (bool success,) = entryPoint.call(abi.encodeWithSignature("withdrawStake(address)", recipient));
        require(success, "EntryPoint withdrawStake failed");
    }

    /**
     * @dev Set spending limit for an address
     * @param spender Address to set limit for
     * @param limit Daily spending limit (0 = no limit)
     */
    function setSpendingLimit(address spender, uint256 limit) external onlyOwner {
        spendingLimits[spender] = limit;
        emit SpendingLimitSet(spender, limit);
    }

    /**
     * @dev Add authorized spender
     * @param spender Address to authorize (use address(0) to allow all)
     */
    function addAuthorizedSpender(address spender) external onlyOwner {
        authorizedSpenders[spender] = true;
        emit AuthorizedSpenderAdded(spender);
    }

    /**
     * @dev Remove authorized spender
     * @param spender Address to remove authorization
     */
    function removeAuthorizedSpender(address spender) external onlyOwner {
        authorizedSpenders[spender] = false;
        emit AuthorizedSpenderRemoved(spender);
    }

    /**
     * @dev Pause the paymaster
     */
    function pause() external onlyOwner {
        paused = true;
        emit PaymasterPaused();
    }

    /**
     * @dev Unpause the paymaster
     */
    function unpause() external onlyOwner {
        paused = false;
        emit PaymasterUnpaused();
    }

    /**
     * @dev Transfer ownership
     * @param newOwner New owner address
     */
    function transferOwnership(address newOwner) external onlyOwner {
        require(newOwner != address(0), "Invalid new owner");
        owner = newOwner;
    }

    /**
     * @dev Get paymaster statistics
     */
    function getStats() external view returns (
        uint256 currentDeposit,
        uint256 totalDeposited,
        uint256 totalSpentAmount,
        bool isPaused,
        uint256 stakInfo
    ) {
        return (
            getDeposit(),
            totalDeposit,
            totalSpent,
            paused,
            0 // Simplified - would need to call getDepositInfo on EntryPoint
        );
    }

    /**
     * @dev Emergency withdraw (in case of contract issues)
     */
    function emergencyWithdraw() external onlyOwner {
        uint256 balance = getDeposit();
        if (balance > 0) {
            (bool success,) = entryPoint.call(abi.encodeWithSignature("withdrawTo(address,uint256)", payable(owner), balance));
            require(success, "Emergency withdraw failed");
        }
    }

    /**
     * @dev Receive ETH and automatically deposit to EntryPoint
     */
    receive() external payable {
        if (msg.value > 0) {
            deposit();
        }
    }

    /**
     * @dev Fallback function
     */
    fallback() external payable {
        revert("Function not found");
    }
}

contracts/utils/UserOperationLib.sol.content:
pragma solidity ^0.8.20;

import "../interfaces/IUserOperation.sol";

/**
 * @title UserOperationLib
 * @dev Utility library for UserOperation hashing and validation
 */
library UserOperationLib {

    /**
     * @dev Calculate hash of UserOperation for signing and validation
     * @param userOp The user operation to hash
     * @param entryPoint The entry point address
     * @param chainId The chain ID
     * @return Hash of the user operation
     */
    function hash(
        IUserOperation.UserOperation memory userOp,
        address entryPoint,
        uint256 chainId
    ) internal pure returns (bytes32) {
        return keccak256(abi.encode(pack(userOp), entryPoint, chainId));
    }

    /**
     * @dev Pack user operation for hashing (excluding signature)
     * @param userOp The user operation to pack
     * @return Packed user operation data
     */
    function pack(IUserOperation.UserOperation memory userOp) internal pure returns (bytes32) {
        address paymaster;
        uint256 paymasterVerificationGasLimit;
        uint256 paymasterPostOpGasLimit;
        bytes memory paymasterData;

        if (userOp.paymasterAndData.length == 0) {
            paymaster = address(0);
            paymasterVerificationGasLimit = 0;
            paymasterPostOpGasLimit = 0;
            paymasterData = "";
        } else {
            // Extract paymaster address (first 20 bytes)
            bytes memory paymasterAndData = userOp.paymasterAndData;
            assembly {
                paymaster := mload(add(paymasterAndData, 20))
            }

            // Extract verification gas limit (bytes 20-52)
            if (paymasterAndData.length >= 52) {
                assembly {
                    paymasterVerificationGasLimit := mload(add(paymasterAndData, 52))
            }
            }

            // Extract post-op gas limit (bytes 52-84)  
            if (paymasterAndData.length >= 84) {
                assembly {
                    paymasterPostOpGasLimit := mload(add(paymasterAndData, 84))
                }
            }

            // Extract remaining data (after byte 84)
            if (paymasterAndData.length > 84) {
                paymasterData = new bytes(paymasterAndData.length - 84);
                for (uint256 i = 0; i < paymasterData.length; i++) {
                    paymasterData[i] = paymasterAndData[i + 84];
                }
            }
        }

        return keccak256(abi.encode(
            userOp.sender,
            userOp.nonce,
            keccak256(userOp.initCode),
            keccak256(userOp.callData),
            userOp.callGasLimit,
            userOp.verificationGasLimit,
            userOp.preVerificationGas,
            userOp.maxFeePerGas,
            userOp.maxPriorityFeePerGas,
            paymaster,
            paymasterVerificationGasLimit,
            paymasterPostOpGasLimit,
            keccak256(paymasterData)
        ));
    }

    /**
     * @dev Get request ID for tracking
     * @param userOp The user operation
     * @return Request ID
     */
    function getRequestId(IUserOperation.UserOperation memory userOp) internal pure returns (uint256) {
        return uint256(keccak256(abi.encodePacked(userOp.sender, userOp.nonce)));
    }

    /**
     * @dev Validate UserOperation structure
     * @param userOp The user operation to validate
     * @return True if valid structure
     */
    function validateStructure(IUserOperation.UserOperation memory userOp) internal pure returns (bool) {
        return userOp.sender != address(0) &&
               userOp.callGasLimit > 0 &&
               userOp.verificationGasLimit > 0 &&
               userOp.maxFeePerGas > 0;
    }

    /**
     * @dev Get gas price for operation
     * @param userOp The user operation
     * @return Effective gas price
     */
    function gasPrice(IUserOperation.UserOperation memory userOp) internal view returns (uint256) {
        unchecked {
            uint256 baseFee = block.basefee;
            uint256 priorityFee = userOp.maxPriorityFeePerGas;
            uint256 maxFee = userOp.maxFeePerGas;

            if (maxFee == priorityFee) {
                // Legacy gas pricing
                return maxFee;
            }

            return min(maxFee, baseFee + priorityFee);
        }
    }

    /**
     * @dev Helper function to get minimum of two values
     */
    function min(uint256 a, uint256 b) internal pure returns (uint256) {
        return a < b ? a : b;
    }
}

